// server.js
const express = require('express');
const app = express();
const port = 3001;

app.use(express.static('dist'));

app.get('/', (req, res) => {
  res.sendFile('index.html', { root: __dirname });
});

app.listen(port, () => {
  console.log(`Server is running on http://localhost:${port}`);
});
